import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import cat1 from "../assets/images/cat1.jpg";
import cat2 from "../assets/images/cat2.jpg";
import cat3 from "../assets/images/cat3.jpg";
import cat4 from "../assets/images/cat4.jpg";
import cat5 from "../assets/images/cat5.jpg";
import cat6 from "../assets/images/cat6.jpg";
import cat7 from "../assets/images/cat7.jpg";
import cat8 from "../assets/images/cat8.jpg";
import cat9 from "../assets/images/cat9.jpg";
import cat10 from "../assets/images/cat10.jpg";

const BuyPage = () => {
  const { animal } = useParams();

  const animalsData = {
    cats: [
      { name: "Persian Cat", image: cat1, description: "Friendly and fluffy Persian cat." },
      { name: "Siamese Cat", image: cat2, description: "Elegant Siamese cat with blue eyes." },
      { name: "Maine Coon", image: cat3, description: "Large and gentle Maine Coon cat." },
      { name: "Bengal Cat", image: cat4, description: "Active and playful Bengal cat." },
      { name: "Ragdoll Cat", image: cat5, description: "Affectionate and calm Ragdoll cat." },
      { name: "British Shorthair", image: cat6, description: "Charming British Shorthair cat." },
      { name: "Abyssinian Cat", image: cat7, description: "Energetic and curious Abyssinian cat." },
      { name: "Sphynx Cat", image: cat8, description: "Unique and hairless Sphynx cat." },
      { name: "Scottish Fold", image: cat9, description: "Adorable Scottish Fold cat with folded ears." },
      { name: "Birman Cat", image: cat10, description: "Sacred and beautiful Birman cat." },
    ],
    // Add similar data for other animals like dogs, rabbits, etc.
  };

  const animalData = animalsData[animal.toLowerCase()] || [];

  return (
    <div>
      <Navbar />
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-3xl font-bold mb-6">Buy Your Favorite {animal.charAt(0).toUpperCase() + animal.slice(1)}</h1>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {animalData.map((item, index) => (
            <div key={index} className="bg-white rounded shadow p-4">
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-48 object-cover rounded mb-4"
              />
              <h2 className="text-xl font-bold mb-2">{item.name}</h2>
              <p className="text-gray-700 mb-4">{item.description}</p>
              <button className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600">
                Buy Now
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BuyPage;
