import React from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "./Navbar";

const Home = () => {
  const navigate = useNavigate();
  const animals = [
    { name: "Cats", emoji: "üê±" },
    { name: "Dogs", emoji: "üê∂" },
    { name: "Rabbits", emoji: "üê∞" },
    { name: "Birds", emoji: "üê¶" },
    { name: "Fish", emoji: "üêü" },
    { name: "Hamsters", emoji: "üêπ" },
    { name: "Turtles", emoji: "üê¢" },
    { name: "Guinea Pigs", emoji: "üêπ" },
  ];

  const options = [
    { name: "For Sale", path: "for-sale" },
    { name: "Adoption", path: "adoption" },
    { name: "Services", path: "services" },
    { name: "Contact", path: "contact" },
  ];

  const animalData = [
    {
      name: "Cute Cat",
      description: "This is a cute cat.",
      image: "https://placekitten.com/400/300",
    },
    {
      name: "Adorable Dog",
      description: "This is an adorable dog.",
      image: "https://placedog.net/400/300",
    },
    {
      name: "Lovely Rabbit",
      description: "This is a lovely rabbit.",
      image: "https://placekitten.com/401/301",
    },
    {
      name: "Charming Bird",
      description: "This is a charming bird.",
      image: "https://placedog.net/401/301",
    },
    {
      name: "Fishy Fish",
      description: "This is a fishy fish.",
      image: "https://placekitten.com/402/302",
    },
    {
      name: "Hammy Hamster",
      description: "This is a hammy hamster.",
      image: "https://placedog.net/402/302",
    },
    {
      name: "Turtley Turtle",
      description: "This is a turtley turtle.",
      image: "https://placekitten.com/403/303",
    },
    {
      name: "Guinea Piggy",
      description: "This is a guinea piggy.",
      image: "https://placedog.net/403/303",
    },
  ];

  return (
    <div>
      <Navbar />
      <div className="flex flex-col items-center justify-center min-h-[calc(100vh-64px)] bg-gray-100">
        <h1 className="text-4xl font-bold">Welcome to PetPal!</h1>
        <p className="mt-4 text-lg text-gray-700 text-center">
          Choose your favorite to buy and explore the various pets.
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {animalData.map((item, index) => (
            <div
              key={index}
              className="bg-white rounded shadow-lg p-4 hover:shadow-xl transition-transform transform hover:scale-105"
            >
              <img
                src={item.image}
                alt={item.name}
                className="w-full h-48 object-cover rounded mb-4"
              />
              <h2 className="text-xl font-bold mb-2">{item.name}</h2>
              <p className="text-gray-700 mb-4">{item.description}</p>
              <button className="px-4 py-2 bg-gradient-to-r from-blue-500 to-blue-700 text-white rounded hover:from-blue-600 hover:to-blue-800">
                Buy Now
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
