import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import catImage from "../assets/images/cat1.jpg";

const BenefitsPage = () => {
  const { animal } = useParams();

  const hospitals = [
    { name: "Happy Paws Clinic", location: "123 Main St, City Center" },
    { name: "Furry Friends Hospital", location: "456 Elm St, Downtown" },
    { name: "Pet Wellness Center", location: "789 Oak St, Suburbs" },
  ];

  const accessories = [
    { name: "Cat Food", price: "$10 (Discounted)" },
    { name: "Cat Toys", price: "$5 (Discounted)" },
    { name: "Cat Bed", price: "$20 (Discounted)" },
  ];

  return (
    <div>
      <Navbar />
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-3xl font-bold mb-6">Exclusive Benefits for {animal.charAt(0).toUpperCase() + animal.slice(1)}</h1>
        <div className="bg-white rounded shadow p-4 mb-6">
          <img
            src={catImage}
            alt={`${animal} benefits`}
            className="w-full h-48 object-cover rounded mb-4"
          />
          <h2 className="text-xl font-bold mb-2">Special Discount</h2>
          <p className="text-gray-700 mb-4">
            Get a 20% discount on the original price of this {animal}.
          </p>
          <h2 className="text-xl font-bold mb-2">Free Medication</h2>
          <p className="text-gray-700 mb-4">
            Enjoy free medication for 4 months at the following hospitals:
          </p>
          <ul className="list-disc list-inside mb-4">
            {hospitals.map((hospital, index) => (
              <li key={index}>
                <strong>{hospital.name}</strong> - {hospital.location}
              </li>
            ))}
          </ul>
          <h2 className="text-xl font-bold mb-2">Discounted Accessories</h2>
          <p className="text-gray-700 mb-4">Get these accessories at a discounted price:</p>
          <ul className="list-disc list-inside">
            {accessories.map((accessory, index) => (
              <li key={index}>
                <strong>{accessory.name}</strong> - {accessory.price}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default BenefitsPage;
