import React from "react";
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import Login from "./Login";
import Signup from "./Signup";
import { auth } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";

const Navbar = () => {
  const navigate = useNavigate();
  const [user] = useAuthState(auth);

  const handleLogout = async () => {
    await auth.signOut();
    navigate("/login");
  };

  return (
    <nav className="flex items-center justify-between px-6 py-4 bg-blue-500 text-white">
      <div className="text-2xl font-bold">PetPal</div>
      <div className="space-x-4">
        <Link to="/" className="hover:underline">
          Home
        </Link>
        {user && (
          <>
            <Link to="/orders" className="hover:underline">
              Your Orders
            </Link>
            <button
              onClick={handleLogout}
              className="px-4 py-2 bg-red-500 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
};

const Home = () => {
  const animals = [
    { name: 'Cats', emoji: '🐱' },
    { name: 'Dogs', emoji: '🐶' },
    { name: 'Rabbits', emoji: '🐰' },
    { name: 'Birds', emoji: '🐦' },
    { name: 'Fish', emoji: '🐟' },
    { name: 'Hamsters', emoji: '🐹' },
    { name: 'Turtles', emoji: '🐢' },
    { name: 'Guinea Pigs', emoji: '🐹' },
  ];

  return (
    <div>
      <Navbar />
      <div className="flex flex-col items-center justify-center min-h-[calc(100vh-64px)] bg-gray-100">
        <h1 className="text-4xl font-bold">Welcome to PetPal!</h1>
        <p className="mt-4 text-lg text-gray-700 text-center">
          Choose your favorite to buy and explore the various pets.
        </p>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mt-8">
          {animals.map((animal) => (
            <div
              key={animal.name}
              className="flex flex-col items-center p-4 bg-white rounded shadow hover:shadow-lg transition-transform transform hover:scale-105 cursor-pointer"
            >
              <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center text-3xl">
                {animal.emoji}
              </div>
              <p className="mt-2 text-lg font-medium text-gray-800">{animal.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/" element={<Home />} />
        <Route path="/orders" element={<h1>Your Orders Page</h1>} />
      </Routes>
    </Router>
  );
};

export default App;
