import React, { useState, useEffect } from "react";
import { useAuthState } from "react-firebase-hooks/auth";
import { auth } from "../firebase";
import Navbar from "./Navbar";
import { useNavigate } from "react-router-dom";

const OrdersPage = () => {
  const [user, loading] = useAuthState(auth);
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!loading) {
      if (!user) {
        // Redirect to login if not authenticated
        navigate("/login");
        return;
      }
      
      // Get user-specific orders from localStorage
      const allOrders = JSON.parse(localStorage.getItem("orders")) || [];
      const userOrders = allOrders.filter(order => order.userId === user.uid);
      
      // Filter out invalid orders
      const validOrders = userOrders.filter(
        (order) => order.image && order.name && order.description
      );
      
      setOrders(validOrders);
    }
  }, [user, loading, navigate]);

  if (loading) {
    return (
      <div>
        <Navbar />
        <div className="p-6 bg-gray-100 min-h-screen">
          <p>Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-3xl font-bold mb-6">Your Orders</h1>
        {orders.length === 0 ? (
          <div className="text-center py-8">
            <p className="text-gray-600 text-lg">No orders yet.</p>
            <p className="text-gray-500 mt-2">Start shopping to see your orders here!</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {orders.map((order, index) => (
              <div key={index} className="bg-white rounded shadow p-4">
                <img
                  src={order.image}
                  alt={order.name}
                  className="w-full h-48 object-cover rounded mb-4"
                />
                <h2 className="text-xl font-bold mb-2">{order.name}</h2>
                <p className="text-gray-700 mb-4">{order.description}</p>
                <div className="text-sm text-gray-500">
                  <p>Original Price: {order.originalPrice}</p>
                  <p className="text-green-600 font-semibold">Paid: {order.discountedPrice}</p>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default OrdersPage;
