import React from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "./Navbar";

const Home = () => {
  const navigate = useNavigate();
  const animals = [
    { name: "Cats", emoji: "üê±" },
    { name: "Dogs", emoji: "üê∂" },
    { name: "Rabbits", emoji: "üê∞" },
    { name: "Birds", emoji: "üê¶" },
    { name: "Fish", emoji: "üêü" },
    { name: "Hamsters", emoji: "üêπ" },
    { name: "Turtles", emoji: "üê¢" },
    { name: "Guinea Pigs", emoji: "üêπ" },
  ];

  const options = [
    { name: "For Sale", path: "for-sale" },
    { name: "Adoption", path: "adoption" },
    { name: "Services", path: "services" },
    { name: "Contact", path: "contact" },
  ];

  return (
    <div>
      <Navbar />
      <div className="flex flex-col items-center justify-center min-h-[calc(100vh-64px)] bg-gray-100">
        <h1 className="text-4xl font-bold">Welcome to PetPal!</h1>
        <p className="mt-4 text-lg text-gray-700 text-center">
          Choose your favorite to buy and explore the various pets.
        </p>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mt-8">
          {options.map((option) => (
            <div
              key={option.path}
              className="flex flex-col items-center p-6 bg-gradient-to-r from-green-100 to-green-300 rounded shadow hover:shadow-lg transition-transform transform hover:scale-105 cursor-pointer"
              onClick={option.action || (() => navigate(`/animal/${animal}/${option.path}`))}
            >
              <p className="text-lg font-medium text-gray-800">{option.name}</p>
            </div>
          ))}
        </div>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-6 mt-8">
          {animals.map((animal) => (
            <div
              key={animal.name}
              className="flex flex-col items-center p-4 bg-gradient-to-r from-blue-100 to-blue-300 rounded shadow hover:shadow-lg transition-transform transform hover:scale-105 cursor-pointer"
              onClick={() => navigate(`/animal/${animal.name.toLowerCase()}`)}
            >
              <div className="w-16 h-16 bg-white rounded-full flex items-center justify-center text-3xl shadow-md">
                {animal.emoji}
              </div>
              <p className="mt-2 text-lg font-medium text-gray-800">{animal.name}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
