import React from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";

const BenefitsPage = () => {
  const { animal } = useParams();

  const animalPricing = {
    cats: {
      originalPrice: "‚Çπ25,000 - ‚Çπ45,000",
      discountedPrice: "‚Çπ20,000 - ‚Çπ36,000",
      savings: "Up to ‚Çπ9,000 OFF"
    },
    dogs: {
      originalPrice: "‚Çπ35,000 - ‚Çπ80,000",
      discountedPrice: "‚Çπ28,000 - ‚Çπ64,000",
      savings: "Up to ‚Çπ16,000 OFF"
    },
    birds: {
      originalPrice: "‚Çπ3,500 - ‚Çπ55,000",
      discountedPrice: "‚Çπ2,800 - ‚Çπ44,000",
      savings: "Up to ‚Çπ11,000 OFF"
    }
  };

  const partnerHospitals = {
    cats: [
      { name: "PetCare Veterinary Hospital", location: "Banjara Hills, Hyderabad", pincode: "500034", services: "24/7 Emergency Care, Vaccination, Surgery" },
      { name: "Paws & Claws Animal Clinic", location: "Jubilee Hills, Hyderabad", pincode: "500033", services: "General Checkup, Dental Care, Emergency Services" }
    ],
    dogs: [
      { name: "Canine Care Veterinary Center", location: "Gachibowli, Hyderabad", pincode: "500032", services: "24/7 Emergency Care, Vaccination, Surgery, Training" },
      { name: "Happy Tails Pet Hospital", location: "Madhapur, Hyderabad", pincode: "500081", services: "General Checkup, Dental Care, Emergency Services, Grooming" }
    ],
    birds: [
      { name: "Avian Care Specialists", location: "HITEC City, Hyderabad", pincode: "500081", services: "Bird-Specific Care, 24/7 Emergency Services, Nutrition Counseling" },
      { name: "Feathered Friends Veterinary Clinic", location: "Kondapur, Hyderabad", pincode: "500084", services: "General Bird Care, Emergency Services, Behavioral Consultation" }
    ]
  };

  const freeAccessories = {
    cats: [
      "Premium Cat Food (4 months supply)",
      "Luxury Cat Bed & Blanket",
      "Interactive Toys & Scratching Posts",
      "Grooming Kit & Litter Box",
      "Cat Carrier for Travel"
    ],
    dogs: [
      "Premium Dog Food (4 months supply)",
      "Comfortable Dog Bed & Blanket",
      "Training Collar & Leash Set",
      "Interactive Toys & Chew Toys",
      "Grooming Kit & Food Bowls"
    ],
    birds: [
      "Premium Bird Seed Mix (4 months supply)",
      "Spacious Bird Cage with Perches",
      "Interactive Toys & Swings",
      "Grooming Kit & Food Dispensers",
      "Travel Carrier for Birds"
    ]
  };

  const services247 = {
    cats: [
      "24/7 Emergency Veterinary Care",
      "Round-the-clock Customer Support",
      "Emergency Pet Transportation",
      "24/7 Health Monitoring Support",
      "Instant Vet Consultation via App"
    ],
    dogs: [
      "24/7 Emergency Veterinary Care",
      "Round-the-clock Customer Support",
      "Emergency Pet Transportation",
      "24/7 Health Monitoring Support",
      "Instant Vet Consultation via App"
    ],
    birds: [
      "24/7 Emergency Avian Care",
      "Round-the-clock Customer Support",
      "Emergency Pet Transportation",
      "24/7 Health Monitoring Support",
      "Instant Bird Specialist Consultation via App"
    ]
  };

  const deliveryInfo = {
    location: "Hyderabad & Surrounding Areas",
    coverage: "All Hyderabad Pincodes: 500001-500084",
    freeDelivery: "FREE Home Delivery within 24 hours",
    areas: [
      "Banjara Hills (500034)",
      "Jubilee Hills (500033)", 
      "Gachibowli (500032)",
      "Madhapur (500081)",
      "HITEC City (500081)",
      "Kondapur (500084)",
      "Kukatpally (500072)",
      "Secunderabad (500003)",
      "And all other Hyderabad areas"
    ]
  };

  // Get current animal data
  const currentAnimal = animal.toLowerCase();
  const currentPricing = animalPricing[currentAnimal] || {};
  const currentHospitals = partnerHospitals[currentAnimal] || [];
  const currentAccessories = freeAccessories[currentAnimal] || [];
  const currentServices = services247[currentAnimal] || [];

  // Capitalize first letter for display
