import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";

const BenefitsPage = () => {
  const { animal } = useParams();
  const [selectedPet, setSelectedPet] = useState("");

  // Comprehensive pricing for all animal types
  const animalPricing = {
    cats: {
      "Persian": { original: 45000, discounted: 36000, savings: 9000 },
      "Maine Coon": { original: 55000, discounted: 44000, savings: 11000 },
      "Siamese": { original: 35000, discounted: 28000, savings: 7000 },
      "British Shorthair": { original: 40000, discounted: 32000, savings: 8000 },
      "Ragdoll": { original: 50000, discounted: 40000, savings: 10000 },
      "Bengal": { original: 60000, discounted: 48000, savings: 12000 },
      "Sphynx": { original: 65000, discounted: 52000, savings: 13000 },
      "Abyssinian": { original: 38000, discounted: 30400, savings: 7600 }
    },
    dogs: {
      "Golden Retriever": { original: 35000, discounted: 28000, savings: 7000 },
      "Labrador": { original: 30000, discounted: 24000, savings: 6000 },
      "German Shepherd": { original: 40000, discounted: 32000, savings: 8000 },
      "Pomeranian": { original: 25000, discounted: 20000, savings: 5000 },
      "Pug": { original: 28000, discounted: 22400, savings: 5600 },
      "Beagle": { original: 22000, discounted: 17600, savings: 4400 },
      "Rottweiler": { original: 45000, discounted: 36000, savings: 9000 },
      "Husky": { original: 50000, discounted: 40000, savings: 10000 }
    },
    rabbits: {
      "Netherland Dwarf": { original: 8000, discounted: 6400, savings: 1600 },
      "Holland Lop": { original: 12000, discounted: 9600, savings: 2400 },
      "Rex": { original: 15000, discounted: 12000, savings: 3000 },
      "Angora": { original: 18000, discounted: 14400, savings: 3600 },
      "Lionhead": { original: 10000, discounted: 8000, savings: 2000 },
      "Flemish Giant": { original: 20000, discounted: 16000, savings: 4000 }
    },
    birds: {
      "Budgerigar": { original: 3000, discounted: 2400, savings: 600 },
      "Cockatiel": { original: 8000, discounted: 6400, savings: 1600 },
      "Lovebird": { original: 5000, discounted: 4000, savings: 1000 },
      "Parakeet": { original: 4000, discounted: 3200, savings: 800 },
      "Canary": { original: 6000, discounted: 4800, savings: 1200 },
      "Macaw": { original: 25000, discounted: 20000, savings: 5000 }
    },
    fish: {
      "Goldfish": { original: 500, discounted: 400, savings: 100 },
      "Betta": { original: 800, discounted: 640, savings: 160 },
      "Guppy": { original: 300, discounted: 240, savings: 60 },
      "Tetra": { original: 400, discounted: 320, savings: 80 },
      "Angelfish": { original: 1200, discounted: 960, savings: 240 },
      "Discus": { original: 3000, discounted: 2400, savings: 600 }
    },
    hamsters: {
      "Syrian": { original: 1500, discounted: 1200, savings: 300 },
      "Dwarf": { original: 1200, discounted: 960, savings: 240 },
      "Roborovski": { original: 1800, discounted: 1440, savings: 360 },
      "Chinese": { original: 1000, discounted: 800, savings: 200 },
      "Campbell": { original: 1300, discounted: 1040, savings: 260 }
    },
    turtles: {
      "Red-Eared Slider": { original: 2000, discounted: 1600, savings: 400 },
      "Painted Turtle": { original: 2500, discounted: 2000, savings: 500 },
      "Box Turtle": { original: 3000, discounted: 2400, savings: 600 },
      "Musk Turtle": { original: 1800, discounted: 1440, savings: 360 },
      "Map Turtle": { original: 2200, discounted: 1760, savings: 440 }
    },
    "guinea pigs": {
      "American": { original: 3000, discounted: 2400, savings: 600 },
      "Abyssinian": { original: 4000, discounted: 3200, savings: 800 },
      "Peruvian": { original: 5000, discounted: 4000, savings: 1000 },
      "Silkie": { original: 4500, discounted: 3600, savings: 900 },
      "Teddy": { original: 3500, discounted: 2800, savings: 700 }
    }
  };

  // Partner hospitals for all animals
  const partnerHospitals = {
    cats: [
      {
        name: "Paws & Care Veterinary Hospital",
        location: "15th Cross, Indiranagar, Bangalore - 560038",
        phone: "+91 80 2345 6789",
        services: ["General Checkup", "Vaccinations", "Emergency Care", "Surgery"],
        rating: "4.8/5"
      },
      {
        name: "PetCare Animal Medical Center",
        location: "100 Feet Road, Koramangala, Bangalore - 560034",
        phone: "+91 80 2987 6543",
        services: ["Specialized Care", "Dental Care", "Diagnostic Services", "24/7 Emergency"],
        rating: "4.9/5"
      }
    ],
    dogs: [
      {
        name: "Canine Care Veterinary Clinic",
        location: "MG Road, Bangalore - 560001",
        phone: "+91 80 1234 5678",
        services: ["Vaccinations", "Training Support", "Emergency Care", "Surgery"],
        rating: "4.7/5"
      },
      {
