import React, { useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import Navbar from "./Navbar";

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [paymentMethod, setPaymentMethod] = useState("");
  const [formData, setFormData] = useState({});

  const purchasedItem = location.state?.item;

  console.log("Purchased Item:", purchasedItem); // Debug log

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleConfirm = () => {
    if (!paymentMethod) {
      alert("Please select a payment method.");
      return;
    }

    // Simulate saving the order
    const orders = JSON.parse(localStorage.getItem("orders")) || [];
    localStorage.setItem("orders", JSON.stringify([...orders, purchasedItem]));

    // Show success popup
    alert("Your purchase was successful!");

    // Navigate to Orders page
    navigate("/orders");
  };

  if (!purchasedItem) {
    return (
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-3xl font-bold mb-6">Error</h1>
        <p className="text-gray-700">
          No item selected for purchase. Please go back and try again.
        </p>
      </div>
    );
  }

  return (
    <div>
      <Navbar />
      <div className="p-6 bg-gray-100 min-h-screen">
        <h1 className="text-3xl font-bold mb-6">Payment</h1>

        {purchasedItem && (
          <div className="bg-white rounded shadow p-4 mb-6">
            <img
              src={purchasedItem.image}
              alt={purchasedItem.name}
              className="w-full h-80 object-contain rounded mb-4"
            />
            <p className="text-gray-700 mb-4">
              {purchasedItem.description || "This breed is known for its friendly and affectionate nature. They are playful, intelligent, and make great companions for families and individuals alike."}
            </p>
            <h2 className="text-xl font-bold mb-2">{purchasedItem.name}</h2>
          </div>
        )}

        <div className="space-y-4">
          <label className="block">
            <input
              type="radio"
              name="payment"
              value="Card"
              onChange={(e) => setPaymentMethod(e.target.value)}
              className="mr-2"
            />
            Card Payment
          </label>
          <label className="block">
            <input
              type="radio"
              name="payment"
              value="UPI"
              onChange={(e) => setPaymentMethod(e.target.value)}
              className="mr-2"
            />
            UPI Payment
          </label>
          <label className="block">
            <input
              type="radio"
              name="payment"
              value="Net Banking"
              onChange={(e) => setPaymentMethod(e.target.value)}
              className="mr-2"
            />
            Net Banking
          </label>
        </div>

        {paymentMethod === "Card" && (
          <div className="space-y-4">
            <div>
              <label className="block text-gray-700 font-medium mb-2">Card Holder Name</label>
              <input
                type="text"
                name="cardHolderName"
                placeholder="Full Name"
                className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                onChange={handleInputChange}
              />
            </div>
            <div>
              <label className="block text-gray-700 font-medium mb-2">Card Number</label>
              <input
                type="text"
                name="cardNumber"
                maxLength="19"
                placeholder="####-####-####-####"
                className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                onChange={(e) => {
                  const value = e.target.value.replace(/[^0-9]/g, "").slice(0, 16);
                  const formattedValue = value.match(/.{1,4}/g)?.join("-") || "";
                  setFormData({ ...formData, cardNumber: formattedValue });
                  e.target.value = formattedValue;
                }}
              />
            </div>
            <div className="flex space-x-4">
              <div className="flex-1">
                <label className="block text-gray-700 font-medium mb-2">Expiry Date</label>
                <input
                  type="text"
                  name="expiryDate"
                  maxLength="5"
                  placeholder="MM/YY"
                  className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  onChange={handleInputChange}
                />
              </div>
              <div className="flex-1">
                <label className="block text-gray-700 font-medium mb-2">CVV</label>
                <input
                  type="password"
                  name="cvv"
                  maxLength="3"
                  placeholder="CVV"
                  className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  onChange={handleInputChange}
                />
              </div>
            </div>
          </div>
        )}

        {paymentMethod === "UPI" && (
          <div className="mt-6 space-y-4">
            <input
              type="text"
              name="upiId"
              placeholder="UPI ID"
              onChange={handleInputChange}
              className="w-full px-4 py-2 border rounded"
            />
          </div>
        )}

        {paymentMethod === "Net Banking" && (
          <div className="mt-6 space-y-4">
            <input
              type="text"
              name="bankName"
              placeholder="Bank Name"
              onChange={handleInputChange}
              className="w-full px-4 py-2 border rounded"
            />
            <input
              type="text"
              name="accountNumber"
              placeholder="Account Number"
              onChange={handleInputChange}
              className="w-full px-4 py-2 border rounded"
            />
          </div>
        )}

        <button
          onClick={handleConfirm}
          className="mt-6 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
        >
          Confirm
        </button>
      </div>
    </div>
  );
};

export default PaymentPage;
